const Modal = ({ product, onClose, onSave }) => {

    const [updatedProduct, setUpdatedProduct] = useState({ ...product });
    const hundlesubmit =()=>{
        onSave(updatedProduct)
        console.log(updatedProduct)
        APIService.updateplants(updatedProduct).then((res)=>{
            console.log(res.data)
        })
    }
    const handleChange = (e) => {
        const { name, value } = e.target;
        setUpdatedProduct((prev) => ({ ...prev, [name]: value }));
    };

    return (
        <div className="modal-backdrop">
            {/* <div className="modal">
                <h2>Edit Product</h2>
           
            </div> */}
            {/* <h2>Edit Product</h2> */}
            <div className="box">
                <h2>Edit Product</h2>
                <label>
                    Name:
                    <input
                        type="text"
                        name="plant_name"
                        value={updatedProduct.plant_name || ""}
                        onChange={handleChange}
                    />
                </label>
                {/* <label>
                    Type:
                    <input
                        type="text"
                        name="plant_type"
                        value={updatedProduct.plant_type || ""}
                        onChange={handleChange}
                    />
                </label> */}
                <label>
                    Price:
                    <input
                        type="number"
                        name="plant_price"
                        value={updatedProduct.plant_price || ""}
                        onChange={handleChange}
                    />
                </label>
                <div className="modal-actions">
                    <button onClick={onClose}>Cancel</button>
                    <button onClick={hundlesubmit}>Save</button>
                </div>
            </div>
        </div>
    );
};


export default ProductList;
